import { Card,Image } from 'semantic-ui-react';
import React, { Component }  from 'react';
var d3 = require("d3-dsv");




export default class ExecutiveBoard extends Component {
OFFICERS_FILE = '../data/Officers.csv';
IMAGES_PATH = '../data/images/';


  individualCard(name,graduation,title,email,fact,image){



    return(
    <Card>
        <Image src={image} wrapped ui={false} />
        <Card.Content>
        <Card.Header>{name}</Card.Header>
        <Card.Meta>
            <span className='graduation'>Class of {graduation}</span>
        </Card.Meta>
        <Card.Description>
            {title} <br></br>
            {email} <br></br>
            {fact}<br></br>
        </Card.Description>
        </Card.Content>
    </Card>
    )
}

    createCards(){
        execBoard = [];
        d3.csvParseRows(this.OFFICERS_FILE, function(execBoard) {
            for (var i = 0; i < this.execBoard.length; i++){
                this.execBoardCards[i] = this.individualCard(this.execBoard.name,this.execBoard.graduation,this.execBoard.title,this.execBoard.email,this.execBoard.fact,this.IMAGES_PATH+this.execBoard.image);
              }
             return this.execBoardCards;
        });
 }
    render(){
        return(
            this.createCards()
        ) 
    }
}
