import React, { Component } from 'react'
import { Grid, Menu, Segment } from 'semantic-ui-react'
import { NavLink } from 'react-router-dom'
import ExecutiveBoard from './ExecutiveBoard'
import {Route } from 'react-router-dom';

export default class About extends Component {
  state = { activeItem: <div>About</div> }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  handleSideBarClick(elementName){
    switch(elementName) {
        case "about":
          this.setState(<div>about</div>);
          break;
        case "executiveboard":
          this.setState(<ExecutiveBoard/>);
          break;
        case "constitution":
         this.setState(<div>const</div>);
        break;
        default:
          this.setState(<div>default</div>);
      }
  }

  render() {
    const { activeItem } = this.state

    return (
      <Grid>
        <Grid.Column width={4}>
          <Menu fluid vertical tabular>
            <Menu.Item
              name='About'
              as={NavLink} to="/about/about"
              active={activeItem === 'about'}
              onClick={this.handleSideBarClick}
            />
            <Menu.Item
              name='Executive Board'
              as={NavLink} to="/about/executiveboard"
              active={activeItem === 'executiveboard'}
              onClick={this.handleSideBarClick}
            />
            <Menu.Item
              name='Consitution and Bylaws'
              as={NavLink} to="/about/constitution"
              active={activeItem === 'constitution'}
              onClick={this.handleSideBarClick}
            />

          </Menu>
        </Grid.Column>

        <Grid.Column stretched width={12}>
          <Segment>
         {}
          </Segment>
        </Grid.Column>

      </Grid>
    )
  }
}
