import React, { Component } from 'react'
import {Grid, GridColumn, GridRow, Image, Segment, Accordion } from 'semantic-ui-react'
import GridColumnSections from '../../GridColumnSections';

export default class EPicnics extends Component {
    IMAGE_PATH = './images/';
    DESCRIPTION = [
        'We host E-Picnics once per semester. Come for music, free beer (for upperclassmen), and free food (for everyone). This event is also when we hand out our famous E-Picnic t-shirts that make all the Trinity students jealous.'
    ]

    sectionMap = new Map([
        ['E-Picnics', this.DESCRIPTION],
        ['', '']
    ])

    infoPanels =[
        {
            key: 'what',
            title: 'What are E-Picnics?',
            content: [
              'Every Friday at 4:30 pm ESG invites a company or campus organization to host an E-Social at Twinnie’s,',
              'located in Fitzpatrick – Ciemas. We provide free beer (for upperclassmen) and free food (for everyone). This',
               'event is a great way to network with companies and other engineers!' ,

            ].join(' '),
          },


        {
            key: 'when',
            title: 'When are E-Picnics?',
            content: [
              'E-Socials occur every Friday between 4:30 and 6:00 pm.  Due to COVID-19, E-Socials have transitioned online,' ,
              'over zoom.  Keep an eye on your class\'  Pratt Facebook page for further details',
            ].join(' '),
          },

          {
            key: 'where',
            title: 'Where are E-Picnics?',
            content: [
              'E-Socials are located at Twinnie\'s Cafe, in E-Quad, near Hudson Hall' ,
            ].join(' '),
          },

          {
            key: 'who',
            title: 'Who are E-Picnics for?',
            content: [
              'Everyone!  I mean...if you\'re in Pratt of course.' ,
            ].join(' '),
          },
    ]

    render(){
        return(
            <div>
                <h2>E-Picnics</h2>
                <Grid>
                    <GridRow className='dynamicRow'>

                      <div className = 'dynamicEl'>
                        <Segment>
                          <Accordion defaultActiveIndex={0} panels={this.infoPanels} />
                        </Segment>
                      </div>
                      </GridRow>

                    <GridRow>
                      <div className = 'dynamicEl'>
                        <Segment>
                        <Image size = 'massive' src={require(this.IMAGE_PATH+'line-for-food.jpeg')}/>
                        </Segment>
                      </div>


                    </GridRow>
                </Grid>
            </div>
        )
    }
}
