import React, { Component } from 'react'
import { Menu, Icon } from 'semantic-ui-react'
import { NavLink } from 'react-router-dom'



export default class NavigationBar extends Component {
  state = { activeItem: 'home' };

   tabColors = ['red', 'orange', 'yellow', 'green', 'blue', 'purple']
   tabIcons = ['info','birthday cake','phone', 'home', 'folder open', 'handshake']
   tabNames = ['About','Events','Contact','Home', 'Student Resources', 'Partner With Pratt']

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  createTabs(){
    var tabs = []

    for (var i = 0; i < this.tabNames.length; i++){
      tabs[i] = (
        <Menu.Item
              as={NavLink} to={this.createLink(this.tabNames[i])}
              name={this.tabNames[i]}
              active={activeItem === this.tabNames[i]}
              onClick={this.handleItemClick}
              color={this.tabColors[i]}
            >
              <Icon name= {this.tabIcons[i]}/>
              {this.tabNames[i]}
         </Menu.Item>
      )
    }
  }

  createLink(name){
    return ('/'+name)
  }

  render() {
    const { activeItem } = this.state;

    return (
      <div className='navBar'>
      <Menu secondary className="navigationBar">
        <Menu.Item
          as={NavLink} to="/about"
          name='about'
          active={activeItem === 'about'}
          onClick={this.handleItemClick}
          color={this.tabColors[0]}
        >
          <Icon name= {this.tabIcons[0]}/>
          About
        </Menu.Item>

        <Menu.Item
         as={NavLink} to="/events"
          name='events'
          active={activeItem === 'events'}
          onClick={this.handleItemClick}
          color={this.tabColors[1]}
        >
          <Icon name= {this.tabIcons[1]}/>
          Events
        </Menu.Item>

        <Menu.Item
        as={NavLink} to="/contact"
          name='contact'
          active={activeItem === 'contact'}
          onClick={this.handleItemClick}
          color={this.tabColors[2]}
        >
          <Icon name= {this.tabIcons[2]}/>
          Contact
        </Menu.Item>

        <Menu.Item
        as={NavLink} to="/home"
          name='home'
          active={activeItem === 'home'}
          onClick={this.handleItemClick}
          color={this.tabColors[3]}
        >
          <Icon name= {this.tabIcons[3]}/>
          Home
        </Menu.Item>

        <Menu.Item
        as={NavLink} to="/studentResources"
          name='studentResources'
          active={activeItem === 'studentResources'}
          onClick={this.handleItemClick}
          color={this.tabColors[4]}
        >
          <Icon name= {this.tabIcons[4]}/>
          Student Resources
        </Menu.Item>

        <Menu.Item
        as={NavLink} to="/partnerWithPratt"
          name='partnerWithPratt'
          active={activeItem === 'partnerWithPratt'}
          onClick={this.handleItemClick}
          color={this.tabColors[5]}
        >
          <Icon name= {this.tabIcons[5]}/>
          Partner With Pratt
        </Menu.Item>
      </Menu>
      </div>
    );
  }
}
