import React, { Component } from 'react'
import { Grid, Menu, Segment } from 'semantic-ui-react'


export default class StudentResources extends Component {
    tabMap = new Map([
        ['Pratt Requirements & General Questions', <div></div>],
        ['First Semester Courses', <div></div>],
        ['Second Semester Courses', <div></div>],
        ['Clubs', <div></div>],
        ['Helpful Links', <div></div>]

    ])

    state = { activeItem: 'Pratt Requirements & General Questions', activeElement: this.tabMap.get('Pratt Requirements & General Questions') }



  handleItemClick = ( name ) => this.setState({ activeItem: name })

  handleClick = (name) => {
      this.handleItemClick(name);
      this.handleSideBarClick(name);
  }

  handleSideBarClick(name){
    this.setState({activeElement: this.tabMap.get(name) });
  }



  createMenu(){
      var tabs = [];
      const { activeItem } = this.state;
      var index = 0;
    for (let key of this.tabMap.keys()){
        tabs[index] = (
            <Menu.Item
              name= {key}
              active={activeItem === key}
              onClick={ e => this.handleClick(key) }
            />
        )
        index+=1;
    }
    return (
        <Menu secondary fluid vertical>
            {tabs}
        </Menu>
    )
  }

  render() {
    return (
      <div className='tabContent'>
      <Grid>
        <Grid.Column width={3}>
            {this.createMenu()}
        </Grid.Column>

        <Grid.Column stretched width={12}>
          <Segment>
          {this.state.activeElement}
          </Segment>
        </Grid.Column>

      </Grid>
      </div>
    )
  }
}
