import React, { Component } from 'react'
import { Grid, Menu, Segment } from 'semantic-ui-react'
import { NavLink } from 'react-router-dom'
import ExecutiveBoard from './ExecutiveBoard'
import {Route } from 'react-router-dom';

export default class About extends Component {
  state = { activeItem: "about", activeElement:<div>Aboutt</div> }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  handleSideBarClick({name}){
    switch(name) {
        case "About":
    this.setState({activeElement: <div>about</div>});
          break;
        case "Executive Board":
    this.setState({activeElement:<ExecutiveBoard/>});
          break;
        case "Constitution and Bylaws":
    this.setState({activeElement:<div>const</div>});
        break;
        default:
    this.setState({activeElement:<div>default</div>});
      }
  }

  render() {
    const { activeItem } = this.state

    return (
      <Grid>
        <Grid.Column width={4}>
          <Menu fluid vertical tabular>
            <Menu.Item
              name='About'
              as={NavLink} to="/about/about"
              active={activeItem === 'about'}
              onClick={this.handleSideBarClick, this.handleItemClick}
            />
            <Menu.Item
              name='Executive Board'
              as={NavLink} to="/about/executiveboard"
              active={activeItem === 'executiveboard'}
              onClick={this.handleSideBarClick, this.handleItemClick}
            />
            <Menu.Item
              name='Consitution and Bylaws'
              as={NavLink} to="/about/constitution"
              active={activeItem === 'constitution'}
              onClick={this.handleSideBarClick, this.handleItemClick}
            />

          </Menu>
        </Grid.Column>

        <Grid.Column stretched width={12}>
          <Segment>
          {this.state.activeElement}
          </Segment>
        </Grid.Column>

      </Grid>
    )
  }
}
