import React, { Component } from 'react'
import { Grid, Menu, Segment } from 'semantic-ui-react'
import { NavLink } from 'react-router-dom'
import ExecutiveBoard from './ExecutiveBoard'
import {Route } from 'react-router-dom';

export default class About extends Component {
  state = { activeItem: "About", activeElement:<div>Aboutt</div> }

  handleItemClick(e, {name}){
    this.setState({activeItem: name});
  }

  handleClick(event){
      this.handleItemClick(event);
      this.handleSideBarClick();
  }

  handleSideBarClick(){
    switch(this.state.activeItem) {
        case "About":
    this.setState({activeElement: <div>about</div>});
          break;
        case "Executive Board":
    this.setState({activeElement:<ExecutiveBoard/>});
          break;
        case "Constitution and Bylaws":
    this.setState({activeElement:<div>const</div>});
        break;
        default:
    this.setState({activeElement:<div>default</div>});
      }
  }

  render() {
    const { activeItem } = this.state.activeItem

    return (
      <Grid>
        <Grid.Column width={4}>
          <Menu fluid vertical tabular>
            <Menu.Item
              name='About'
              active={activeItem === 'About'}
              onClick={this.handleClick}
            />
            <Menu.Item
              name='Executive Board'
              active={activeItem === 'Executive Board'}
              onClick={(name) => {
                this.handleItemClick(name);
                this.handleSideBarClick();

              }}
            />
            <Menu.Item
              name='Constitution and Bylaws'
              active={activeItem === 'Constitution and Bylaws'}
              onClick={() => {
                this.handleItemClick();
                this.handleSideBarClick();

              }}
            />

          </Menu>
        </Grid.Column>

        <Grid.Column stretched width={12}>
          <Segment>
          {this.state.activeElement}
          </Segment>
        </Grid.Column>

      </Grid>
    )
  }
}
