import React, { useState } from 'react'
import { Grid, Menu} from 'semantic-ui-react'


export default function SideBarMenu () {
    var tabMap = new Map();
    const [activeItem, setActiveItem] = useState('');
    const [activeElement, setActiveElement] = useState(<div/>);
    state = { activeItem: '', activeElement: <div></div>}

   function setMap(map){
       this.tabMap = map;
       this.handleDefaultTab();
   }

   function handleDefaultTab(){
    for (let key of this.tabMap.keys()){
        this.state = ({ activeItem: key, activeElement: this.tabMap.get(key) });
        break;
    } 
   }

  var handleItemClick = ( name ) => this.setState({ activeItem: name })

  var handleClick = (name) => {
      this.handleItemClick(name);
      this.handleSideBarClick(name);
  }

  function handleSideBarClick(name){
    this.setState({activeElement: this.tabMap.get(name) });
  }



  function createMenu(){
      var tabs = [];
      const { activeItem } = this.state;
      var index = 0;
    for (let key of this.tabMap.keys()){
        tabs[index] = (
            <Menu.Item
              key = {key}
              name= {key}
              active={activeItem === key}
              onClick={ e => this.handleClick(key) }
            />
        )
        index+=1;
    }
    return (
        <Menu secondary fluid vertical>
            {tabs}
        </Menu>
    )
  }


    return (
      <div className='tabContent'>
      <Grid>
        <Grid.Column width={3}>
            {this.createMenu()}
        </Grid.Column>

        <Grid.Column stretched width={12}>

          {this.state.activeElement}

        </Grid.Column>

      </Grid>
      </div>
    )

}
